/* Sélection des éléments HTML */
let link=document.getElementById("link"),burger=document.getElementById("burger"),ul=document.querySelector("ul")
/* gestionnaire d'événement sur le a#link pour venir changer l'attribution de la classe .open à la ul et au span#burger */;link.addEventListener("click",function(e){burger.classList.toggle("open"),ul.classList.toggle("open")});