function toggleCollapse(e,o){let n=document.querySelectorAll("."+e);n.forEach((e,l)=>{let t=document.querySelectorAll("."+o)[l];e.addEventListener("click",()=>{"none"===t.style.display||""===t.style.display?(t.style.display="block",n.textContent="Voir moins -"):(t.style.display="none",n.textContent="Voir plus +")})})}toggleCollapse("collapsible","content_collapse");